const $ = new Env('京东炸年兽_瓜分');
//Node.js用户请在jdCookie.js处填写京东ck;
const jdCookieNode = $.isNode() ? require('./jdCookie.js') : '';

$.functionCode = 'promote'
//IOS等用户直接用NobyDa的jd cookie
let cookiesArr = [];
$.cookie = '';
if ($.isNode()) {
    Object.keys(jdCookieNode).forEach((item) => {
        cookiesArr.push(jdCookieNode[item])
    })
    if (process.env.JD_DEBUG && process.env.JD_DEBUG === 'false') console.log = () => { };
} else {
    cookiesArr = [$.getdata('CookieJD'), $.getdata('CookieJD2'), ...jsonParse($.getdata('CookiesJD') || "[]").map(item => item.cookie)].filter(item => !!item);
}

$.appid = 'o2_act';
$.UA = ''
$.UUID = ''
let utils = ''
!(async () => {
    if (!cookiesArr[0]) {
        $.msg($.name, '【提示】请先获取京东账号一cookie\n直接使用NobyDa的京东签到获取', 'https://bean.m.jd.com/bean/signIndex.action', { "open-url": "https://bean.m.jd.com/bean/signIndex.action" });
        return;
    }
    $.endFlag = false
    for (let i = 0; i < cookiesArr.length && true; i++) {
        if (cookiesArr[i]) {
            $.index = i + 1;
            $.cookie = cookiesArr[i];
            $.UserName = decodeURIComponent($.cookie.match(/pt_pin=([^; ]+)(?=;?)/) && $.cookie.match(/pt_pin=([^; ]+)(?=;?)/)[1]);
            $.isLogin = true;
            $.nickName = $.UserName;
            $.hotFlag = false; //是否火爆
            $.uid = ''
            console.log(`\n*****开始【京东账号${$.index}】${$.nickName || $.UserName}*****\n`);
            getUA()
            await movement()
            if ($.endFlag) break
        }
    }


})()
    .catch((e) => {
        $.log(`❌ ${$.name}, 失败! 原因:`)
        console.log(e)
    })
    .finally(() => {
        $.done();
    })


async function movement() {
    try {
        $.signSingle = {};
        $.homeData = {};
        $.taskList = [];
        $.shopSign = ``;
        $.userInfo = ''
        $.fullFlag = false
        await takePostRequest($.functionCode + '_getHomeData');
        if ($.hotFlag) {
            return
        }
        if ($.homeData.result) {
            $.homeMainInfo = $.homeData.result.homeMainInfo
            $.raiseInfo = $.homeMainInfo.raiseInfo
            $.fullFlag = $.raiseInfo.fullFlag
            if (!$.fullFlag) {
                if ($.raiseInfo.buttonStatus == 5) {
                    console.log(`分红:${$.raiseInfo.redInfo.red}份`)
                    await takePostRequest($.functionCode + '_mainDivideRedPacket');
                } else if ($.raiseInfo.buttonStatus == 3) {
                    console.log("等待瓜分")
                    $.endFlag = true
                } else if ($.raiseInfo.buttonStatus == 7) {
                    console.log(`已瓜分红包：${$.raiseInfo.redInfo.red}🧧`)
                } else {
                    console.log('无法瓜分 ' + $.raiseInfo.buttonStatus)
                }
            }
        }
        await $.wait(2000)
    } catch (e) {
        $.logErr(e)
    }
}

async function takePostRequest(type) {
    let body = ``;
    let myRequest = ``;
    let client = 'wh5'
    let clientVersion = '-1'
    let appid = 'signed_wh5'
    switch (type) {
        case $.functionCode + '_mainDivideRedPacket':
        case $.functionCode + '_getHomeData':
            body = `functionId=${type}&client=${client}&clientVersion=${clientVersion}&appid=${appid}&body={}`;
            myRequest = await getPostRequest(type, body);
            break;
        default:
            console.log(`错误${type}`);
    }
    if (myRequest) {
        return new Promise(async resolve => {
            $.post(myRequest, async (err, resp, data) => {
                try {
                    // console.log(data);
                    await dealReturn(type, data);
                } catch (e) {
                    $.logErr(e, resp)
                } finally {
                    resolve();
                }
            })
        })
    }
}


async function dealReturn(type, res) {
    try {
        data = JSON.parse(res);
    } catch (e) {
        console.log(`返回异常：${res}`);
        return;
    }
    switch (type) {
        case $.functionCode + '_getHomeData':
            if (data.code === 0 && data.data && data.data.result) {
                if (data.data['bizCode'] === 0) {
                    $.homeData = data.data;
                }
            } else if (data.data && data.data.bizMsg) {
                if (data.data.bizCode === -1001) {
                    $.hotFlag = true;
                }
                console.log(data.data.bizMsg);
            } else {
                if (data.code == -30001) {
                    $.hotFlag = true;
                }
                console.log(res);
            }
            break;
        case $.functionCode + '_mainDivideRedPacket':
            if (data.data['bizCode'] === 0) {
                console.log(`瓜分红包：${data.data.result.value}🧧`)
            } else {
                console.log(data)
            }
            break;
        default:
            console.log(`未判断的异常:${type}\n${res}`);
    }
}

async function getPostRequest(type, body) {
    let url = `https://api.m.jd.com/`;
    const method = `POST`;
    let cookies = $.cookie.replace(/;([^\s])/g, '; $1')
    let headers = {
        "Accept": "application/json",
        "Accept-Encoding": "gzip, deflate, br",
        "Accept-Language": "zh-CN,zh;q=0.9,en;q=0.8",
        "Content-Type": "application/x-www-form-urlencoded",
        'Cookie': `${cookies} pwdt_id=${encodeURIComponent($.UserName)};`,
        "Origin": "https://wbbny.m.jd.com",
        "Referer": "https://wbbny.m.jd.com/pb/013975090/S9sfBnz3XbRM76wSxkdecndL888/index.html",
        "User-Agent": $.UA,
    };
    headers.Cookie = headers.Cookie.replace(/;\s*$/, '')
    headers.Cookie = headers.Cookie.replace(/;([^\s])/g, '; $1')
    return { url: url, method: method, headers: headers, body: body, timeout: 30000 };
}
function getUA() {
    $.uid = randomString(40)
    $.UA = `jdapp;iPhone;10.1.4;14.3;${$.uid};network/wifi;model/iPhone8,1;addressid/;appBuild/167814;jdSupportDarkMode/0;Mozilla/5.0 (iPhone; CPU iPhone OS 14_3 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Mobile/15E148;supportJDSHWK/1`
}

function randomString(e) {
    e = e || 32;
    let t = "abcdef0123456789", a = t.length, n = "";
    for (i = 0; i < e; i++)
        n += t.charAt(Math.floor(Math.random() * a));
    return n
}
/**
 * 随机从一数组里面取
 * @param arr
 * @param count
 * @returns {Buffer}
 */
function getRandomArrayElements(arr, count) {
    var shuffled = arr.slice(0), i = arr.length, min = i - count, temp, index;
    while (i-- > min) {
        index = Math.floor((i + 1) * Math.random());
        temp = shuffled[index];
        shuffled[index] = shuffled[i];
        shuffled[i] = temp;
    }
    return shuffled.slice(min);
}


function jsonParse(str) {
    if (typeof str == "string") {
        try {
            return JSON.parse(str);
        } catch (e) {
            console.log(e);
            $.msg($.name, '', '请勿随意在BoxJs输入框修改内容\n建议通过脚本去获取cookie')
            return [];
        }
    }
}

